package basic;

public class SumClass {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int first=25;
		int second=20;
		System.out.println("enter first number:" +first);
		System.out.println("enter first number:" +second);
	
		
		System.out.println("sum is:" +(first+ second));
		System.out.println("substraction is:" +(first - second));
		System.out.println("multiplication:" +(first*second));
		System.out.println("division is:" +(first/second));
		System.out.println("remainder is:" +(first%second));
	}

}
